import{SDKModel}from'../sdk/sdk.js';import{Widget,Fragment,UIUtils}from'../ui/ui.js';Devices.DevicesView=class extends Widget.VBox{constructor(){super(true);const deprecationMessage=this.contentElement.createChild('span');const documentationLink=Fragment.html`<a class="devtools-link" role="link" tabindex="0" href="#" style="display: inline; cursor: pointer;">chrome://inspect/#devices</a>`;self.onInvokeElement(documentationLink,event=>{SDKModel.TargetManager.instance().mainTarget().pageAgent().navigate('chrome://inspect/#devices');event.consume(true);});deprecationMessage.style.padding='5px';deprecationMessage.appendChild(UIUtils.formatLocalized('This panel has been deprecated in favor of the %s interface, which has equivalent functionality.',[documentationLink]));this.setDefaultFocusedElement(documentationLink);}};